def revappend[A]( xs: List[A], ys: List[A]): List[A] = match xs { case Nil =&gt; Nil case x::xs_ =&gt; revappend(xs_, x::ys) } ===
def length(xs: List[_]): Int = xs match { case Nil =&gt; Nil case x::xs_ =&gt; 1 + length(xs_) } ===
def append[A]( xs: List[A], ys: List[A]): List[A] = xs match { case Nil =&gt; Nil case x::xs_ =&gt; x :: append(xs_, ys) } append(List(1, 2, 3), List(4, 5, 6)) // =&gt; List(1, 2, 3)  ===
